import React, { Component } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  TouchableOpacity,
  Alert,
  FlatList,
  ToastAndroid,
  ScrollView,
  SectionList,
  Linking,
  Image,
} from "react-native";

import { Container, Thumbnail, Content, Spinner } from "native-base";

import Card from "./item";

import fire from "../Firebase";
import "firebase/firestore";
import FontAwesome from "react-native-vector-icons/FontAwesome5";

import * as ImagePicker from "expo-image-picker";
import PP from "./profileplaceholder";
import { Rating, AirbnbRating } from "react-native-ratings";

export default class Profile extends Component {
  _isMounted = false;
  state = {
    username: "",
    game: 0,
    movies: 0,
    music: 0,
    books: 0,
    gameData: [],
    movieData: [],
    musicData: [],
    bookData: [],
    loading: true,
    avatar: null,
  };

  isSectionsEmpty(sectionsObject) {
    //logic for deciding if sections object is empty goes here
  }

  setPhotoUri = (uri) => {
    fire
      .auth()
      .currentUser.updateProfile({
        photoURL: uri,
      })
      .then(() => {
        fire
          .firestore()
          .collection("USERS")
          .doc(fire.auth().currentUser.uid.toString())
          .update({
            profileUri: uri,
          });
      })
      .then(() => {
        this.setState({
          avatar: uri,
        });
        ToastAndroid.show("Profile Picture updated!", ToastAndroid.SHORT);
      });
  };

  pickImagehandler = async () => {
    let permissionResult = await ImagePicker.requestCameraRollPermissionsAsync();

    if (permissionResult.granted === false) {
      alert("Permission to access camera roll is required!");
      return;
    }

    let pickerResult = await ImagePicker.launchImageLibraryAsync();

    if (pickerResult.cancelled === true) {
      return;
    }
    this.setPhotoUri(pickerResult.uri);
  };

  emptyComponent = () => {
    return (
      <View style={{ flex: 1, alignItems: "center" }}>
        <Text style={{ fontSize: 20, fontStyle: "italic", fontWeight: "bold" }}>
