  userInfo() {
    var userCheck = this.props.route.params.ID != "";

    var uri = null;

    if (userCheck) {
      fire
        .firestore()
        .collection("USERS")
        .doc(this.props.route.params.ID)
        .get()
        .then((doc) => {
          uri = doc.data().profileUri;
        })
        .catch((error) => {
          console.error(error);
        });
    } else {
      fire
        .firestore()
        .collection("USERS")
        .doc(fire.auth().currentUser.uid.toString())
        .get()
        .then((doc) => {
          uri = doc.data().profileUri;
        })
        .catch((error) => {
          console.error(error);
        });
    }
    fire
      .firestore()
      .collection("USERS")
      .doc(
        userCheck
          ? this.props.route.params.ID
          : fire.auth().currentUser.uid.toString()
      )
      .get()
