class PublicItems extends Component {
  state = {
    data: null,
    refreshing: false,
    loading: true,
  };

  handleRefresh = () => {
    this.setState(
      {
        refreshing: true,
      },
      () => {
        this.componentDidMount();
      }
    );
  };

  deleteItem(itemTitle) {
    var docID;

    fire
      .firestore()
      .collection("USERS")
      .doc(fire.auth().currentUser.uid.toString())
      .collection(this.props.name)
      .where("title", "==", itemTitle)
      .get()
      .then((sub) => {
        if (sub.docs.length > 0) {
          sub.forEach((doc) => {
            docID = doc.id;
          });
        }
      })
      .then(() => {
        fire
          .firestore()
          .collection("USERS")
          .doc(fire.auth().currentUser.uid.toString())
          .collection(this.props.name)
          .doc(docID)
          .delete()
          .then(() => {
            Alert.alert("Successfully deleted!");
            this.componentDidMount();
          })
          .catch((err) => {
            Alert.alert(err.toString());
          });
      })
      .catch((err) => {
        Alert.alert(err.toString());
      });
  }

  changeToPrivate = (itemTitle) => {
    var docID;

    fire
      .firestore()
      .collection("USERS")
      .doc(fire.auth().currentUser.uid.toString())
      .collection(this.props.name)
      .where("title", "==", itemTitle)
      .get()
      .then((sub) => {
        if (sub.docs.length > 0) {
          sub.forEach((doc) => {
            docID = doc.id;
          });
        }
      })
      .then(() => {
        fire
          .firestore()
          .collection("USERS")
          .doc(fire.auth().currentUser.uid.toString())
          .collection(this.props.name)
          .doc(docID)
          .update({
            public: false,
          })
          .then(() => {
            // Alert.alert("Successfully Updated!");
            // Toast.show({
            //   text: "Wrong password!",
            //   buttonText: "Okay",
            //   type: "success",
            // });
            ToastAndroid.show("Successfully updated!", ToastAndroid.SHORT);

            this.componentDidMount();
          })
          .catch((err) => {
            Alert.alert(err.toString());
          });
      })
      .catch((err) => {
        Alert.alert(err.toString());
      });
  };
  emptyComponent = () => {
    return (
      <View
        style={{
          flex: 1,
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Text
          style={{
            fontSize: 20,
            fontStyle: "italic",
            fontWeight: "bold",
            color: "white",
          }}
        >
          Nothing here, come back later...
        </Text>
      </View>
    );
  };

  componentDidMount() {
    LogBox.ignoreLogs(["VirtualizedLists should never be nested"]);

    fire
      .firestore()
      .collection("USERS")
      .doc(fire.auth().currentUser.uid.toString())
      .collection(this.props.name)
      .where("public", "==", true)
      .get()
      .then((sub) => {
        if (sub.docs.length > 0) {
          const data = [];
          sub.forEach((doc) => {
            const x = doc.data();
            data.push(x);
          });
          this.setState({
            data: data,
            refreshing: false,
            loading: false,
          });
          console.log(this.state.loading);
        } else {
          this.setState({
            loading: false,
            refreshing: false,
          });
        }
      })

      .catch((err) => {
        return err.toString();
      });
  }

  render() {
    return !this.state.loading ? (
      <FlatList
        data={this.state.data}
        renderItem={({ item }) => (
          <Card>
            <Text style={{ fontSize: 20, fontWeight: "bold", color: "white" }}>
              {item.title}
            </Text>
            <Text style={{ color: "white" }}>
              Description: {item.description}
            </Text>

            <Text style={{ color: "white" }}>Year : {item.year}</Text>
            <View
              style={{
                marginVertical: "1%",
                alignItems: "flex-start",
                flexDirection: "row",
              }}
            >
              <Text style={{ color: "white", marginRight: "1%" }}>
                Rating: {item.rating}
              </Text>
              <Rating
                ratingCount={5}
                readonly
                imageSize={20}
                startingValue={item.rating}
              />
            </View>
            <View
              style={{
                flexDirection: "row",
                justifyContent: "space-between",
              }}
            >
              <TouchableOpacity
                style={{ marginTop: "4%" }}
                onPress={() => this.changeToPrivate(item.title)}
              >
                <Text style={{ fontSize: 15, color: "#009BFF" }}>
                  Make Private
                </Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{ marginTop: "4%", marginRight: "5%" }}
                onPress={() => this.deleteItem(item.title)}
              >
                <Text style={{ fontSize: 15, color: "red" }}>Delete</Text>
              </TouchableOpacity>
            </View>
          </Card>
        )}
        keyExtractor={(item) => item.title}
        extraData={this.state.data}
        refreshing={this.state.refreshing}
        onRefresh={this.handleRefresh}
        ListEmptyComponent={this.emptyComponent}
      />
    ) : (
      <Container style={{ backgroundColor: "black" }}>
        <Content>
          <Spinner color="green" />
        </Content>
      </Container>
    );
  }
}

