import React, { Component } from "react";
import { View, Animated, StyleSheet, TextInput, Text } from "react-native";

export default class FloatingTitleTextInputField extends Component {
  _animatedIsFocused = new Animated.Value(this.props.test === "" ? 0 : 1);
  state = {
    isFocused: false,
    requiredMessage: "This field is required!",
  };

  componentDidUpdate() {
    Animated.timing(this._animatedIsFocused, {
      toValue: this.state.isFocused || this.props.test !== "" ? 1 : 0,
      duration: 200,
      useNativeDriver: false,
    }).start();
  }

  handleFocus = () => {
    this.setState({ isFocused: true });
  };
  handleBlur = () => {
    this.setState({ isFocused: false });
  };

  render() {
    const { label, ...props } = this.props;
    const { isFocused } = this.state;
    const labelStyle = {
      position: "absolute",
      left: 5,
      // transform: [
      //   {
      //     translateY: this._animatedIsFocused.interpolate({
      //       inputRange: [0, 1],
      //       outputRange: [18, 0],
      //     }),
      //   },
      // ],
      top: this._animatedIsFocused.interpolate({
        inputRange: [0, 1],
        outputRange: [18, 0],
      }),

      fontSize: this._animatedIsFocused.interpolate({
        inputRange: [0, 1],
        outputRange: [18, 14],
      }),
      color: this._animatedIsFocused.interpolate({
        inputRange: [0, 1],
        outputRange: ["gray", "#BB86FC"],
      }),
    };
    return this.props.label != "Description" ? (
      <View
        style={{
          width: "80%",

        }}
      >
        <View
          style={{
            backgroundColor: "#272727",
            borderTopEndRadius: 5,
            borderTopStartRadius: 5,
            paddingTop: 18,
          }}
        >
          <Animated.Text style={labelStyle}>{label}</Animated.Text>
          <TextInput
            {...props}
            style={{
              height: 30,
              fontSize: 15,
              color: "white",
              borderBottomWidth: 2,
              paddingLeft: 5,
              borderBottomColor:
                isFocused || this.props.test !== "" ? "#BB86FC" : "gray",
            }}
            onFocus={this.handleFocus}
            onBlur={this.handleBlur}
          ></TextInput>
        </View>
        {isFocused && this.props.test === "" ? (
          <Text style={{ fontSize: 15, color: "red" ,marginTop:2,fontStyle:"italic"}}>
            This is a required field!
          </Text>
        ) : null}
      </View>
    ) : (
      <View
        style={{
          backgroundColor: "#272727",
          width: "80%",
          borderTopEndRadius: 5,
          borderTopStartRadius: 5,
          paddingTop: 18,
        }}
      >
        <Animated.Text style={labelStyle}>{label}</Animated.Text>
        <TextInput
          {...props}
          style={{
            height: 150,
            fontSize: 15,
            color: "white",
            borderBottomWidth: 2,
            borderBottomColor:
              isFocused || this.props.test !== "" ? "#BB86FC" : "gray",
            paddingLeft: 5,
          }}
          onFocus={this.handleFocus}
          onBlur={this.handleBlur}
        ></TextInput>
      </View>
    );
  }
}

const Styles = StyleSheet.create({
  container: {
    width: "80%",
    borderRadius: 3,
    borderStyle: "solid",
    borderWidth: 0.5,
    height: "13%",
    marginVertical: "5%",
    backgroundColor: "#272727",
    borderBottomWidth: 2,
    borderBottomColor: "red",
  },
  textInput: {
    fontSize: 15,
    marginTop: "1%",

    color: "black",
  },
  titleStyles: {
    position: "absolute",
    color: "white",

    left: 3,
    left: 4,
  },
});
