import React, { Component } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Button,
  TouchableOpacity,
  Pressable,
  TextInput,
  Alert,
  ScrollView,
  Switch,
  ToastAndroid,
} from "react-native";
import { Rating, AirbnbRating } from "react-native-ratings";

import fire from "../Firebase";
import "firebase/firestore";

export default class Movies extends Component {
  state = {
    title: this.props.route.params.title,
    genre: "",
    year: 0,
    description: "",
    public: false,
    errorMessage: "",
    rating: 0,
  };

  handleAdd = () => {
    if (this.state.title == "") {
      Alert.alert("Please fill in all the required (*) fields.");
    } else if (this.state.rating < 1) {
      Alert.alert("Rating cannot be less than 1.");
    } else {
      fire
        .firestore()
        .collection("USERS")
        .doc(fire.auth().currentUser.uid.toString())
        .collection("Movies")
        .doc()
        .set({
          title: this.state.title,

          genre: this.state.genre,
          year: this.state.year,

          description: this.state.description,
          public: this.state.public,
          rating: this.state.rating,
        })
        .then(() => {
          ToastAndroid.show("Movie successfully added!", ToastAndroid.SHORT);

          this.props.navigation.navigate("Home");
        })
        .catch((err) => {
          Alert.alert(err.toString());
        });
    }
  };

  render() {
    return (
      <SafeAreaView style={styles.container}>
        <ScrollView>
          <View style={{ marginLeft: "10%" }}>
            <Text style={{ marginVertical: 10, color: "yellow", fontSize: 15 }}>
              {this.state.errorMessage}
            </Text>
            <View>
              <View style={{ flexDirection: "row" }}>
                <Text
                  style={{ fontWeight: "bold", fontSize: 15, color: "white" }}
                >
                  Title
                </Text>
                <Text style={{ color: "red" }}>*</Text>
              </View>
              {this.props.route.params.title == "" ? (
                <TextInput
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  placeholder="Title"
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      title: val.toLowerCase().trim(),
                    });
                  }}
                ></TextInput>
              ) : (
                <TextInput
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  placeholder="Title"
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      title: val.toLowerCase().trim(),
                    });
                  }}
                  defaultValue={this.props.route.params.title}
                ></TextInput>
              )}
            </View>

            <View style={{ marginVertical: "5%" }}>
              <Text
                style={{ fontWeight: "bold", fontSize: 15, color: "white" }}
              >
                Genre
              </Text>
              <TextInput
                style={{
                  height: 40,
                  borderColor: "white",
                  borderWidth: 2,
                  borderRadius: 5,
                  marginRight: 100,
                  marginVertical: "2%",
                  padding: "2%",
                  backgroundColor: "#272727",
                  color: "white",
                }}
                placeholder="Genre"
                placeholderTextColor="gray"
                onChangeText={(val) => {
                  this.setState({
                    genre: val.toLowerCase().trim(),
                  });
                }}
              ></TextInput>
            </View>
            <View style={{ marginVertical: "5%" }}>
              <Text
                style={{ fontWeight: "bold", fontSize: 15, color: "white" }}
              >
                Year Released
              </Text>
              <TextInput
                style={{
                  height: 40,
                  borderColor: "white",
                  borderWidth: 2,
                  borderRadius: 5,
                  marginRight: 100,
                  marginVertical: "2%",
                  padding: "2%",
                  backgroundColor: "#272727",
                  color: "white",
                }}
                placeholder="Year"
                placeholderTextColor="gray"
                maxLength={4}
                keyboardType="number-pad"
                onChangeText={(val) => {
                  this.setState({
                    year: val.trim(),
                  });
                }}
              ></TextInput>
            </View>

            <View style={{ marginVertical: "5%" }}>
              <Text
                style={{ fontWeight: "bold", fontSize: 15, color: "white" }}
              >
                Description
              </Text>
              <TextInput
                style={{
                  height: 150,
                  borderColor: "white",
                  borderWidth: 2,
                  borderRadius: 5,
                  marginRight: 100,
                  marginVertical: "2%",
                  padding: "2%",
                  backgroundColor: "#272727",
                  color: "white",
                }}
                placeholder="Description"
                placeholderTextColor="gray"
                multiline={true}
                onChangeText={(val) => {
                  this.setState({
                    description: val.trim(),
                  });
                }}
              ></TextInput>
              <View
                style={{
                  justifyContent: "flex-start",
                  alignItems: "center",
                  flexDirection: "row",
                  marginVertical: "5%",
                }}
              >
                <Rating
                  type="custom"
                  ratingBackgroundColor="gray"
                  ratingCount={5}
                  fractions={1}
                  minValue={1}
                  startingValue={0}
                  imageSize={30}
                  onFinishRating={(rating) => {
                    this.setState({
                      rating: rating,
                    });
                    // console.log(rating);
                  }}
                  showRating={true}
                  style={{ backgroundColor: "black" }}
                />
                <Text style={{ color: "red", marginRight: "5%" }}>*</Text>
              </View>

              <View
                style={{
                  marginTop: "5%",
                  flexDirection: "row",
                  justifyContent: "flex-start",
                }}
              >
                <Text
                  style={{ fontSize: 15, fontWeight: "bold", color: "white" }}
                >
                  Make Item Public
                </Text>
                <Switch
                  style={{ marginLeft: "10%" }}
                  trackColor={{ false: "#767577", true: "#81b0ff" }}
                  thumbColor={this.state.public ? "#f5dd4b" : "#f4f3f4"}
                  onValueChange={() => {
                    this.setState({
                      public: !this.state.public,
                    });
                  }}
                  value={this.state.public}
                />
              </View>
            </View>
            <View style={{ marginLeft: "15%" }}>
              <TouchableOpacity
                style={{
                  backgroundColor: "#BB86FC",
                  borderRadius: 5,
                  height: "23%",
                  width: "50%",
                  justifyContent: "center",
                  alignItems: "center",
                  marginVertical: "5%",
                }}
                onPress={this.handleAdd}
              >
                <Text style={{ fontSize: 20 }}>ADD</Text>
              </TouchableOpacity>
              <View style={{ marginVertical: "5%" }}></View>
            </View>
          </View>
        </ScrollView>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "black",
  },
});
