import React, { Component } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Button,
  TouchableOpacity,
  Pressable,
  TextInput,
  Alert,
  ScrollView,
  Switch,
  ToastAndroid,
  KeyboardAvoidingView,
} from "react-native";
import { Rating } from "react-native-ratings";
import FloatingTextInput from "./Floating";
import FloatingTextBox from "./FloatingScan";

import fire from "../Firebase";
import "firebase/firestore";

export default class Games extends Component {
  state = {
    title: this.props.route.params.title,
    company: this.props.route.params.brand,
    genre: "",
    year: "",
    platform: this.props.route.params.platform,
    description: "",
    public: false,
    errorMessage: "",
    rating: 0,
  };

  handleAdd = () => {
    if (
      this.state.title == "" ||
      this.state.company == "" ||
      this.state.platform == ""
    ) {
      Alert.alert("Please fill all the required (*) fields.");
    } else if (this.state.rating < 1) {
      Alert.alert("Rating cannot be less than 1.");
    } else {
      fire
        .firestore()
        .collection("USERS")
        .doc(fire.auth().currentUser.uid.toString())
        .collection("Games")
        .doc()
        .set({
          title: this.state.title,
          company: this.state.company,
          genre: this.state.genre,
          year: this.state.year,
          platform: this.state.platform,
          description: this.state.description,
          public: this.state.public,
          rating: this.state.rating,
        })
        .then(() => {
          ToastAndroid.show("Game successfully added!", ToastAndroid.SHORT);
          this.props.navigation.navigate("Home");
        })
        .catch((err) => {
          Alert.alert(err.toString());
        });
    }
  };

  render() {
    return (
      <KeyboardAvoidingView style={styles.container} enabled={true}>
        <ScrollView
          contentContainerStyle={{ flexGrow: 1 }}
          style={{ flex: 1, backgroundColor: "black" }}
        >
          <View>
            <View>
              <Text
                style={{ marginVertical: 10, color: "yellow", fontSize: 15 }}
              >
                {this.state.errorMessage}
              </Text>
            </View>

            <View>
              {this.props.route.params.title == "" ? (
                <FloatingTextBox
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  label="Title"
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      title: val.toLowerCase().trim(),
                    });
                  }}
                  test={this.state.title}
                ></FloatingTextBox>
              ) : (
                <FloatingTextBox
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  label="Title"
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      title: val.toLowerCase().trim(),
                    });
                  }}
                  defaultValue={this.props.route.params.title}
                  test={this.state.title}
                ></FloatingTextBox>
              )}
            </View>
            <View style={{ marginVertical: "5%" }}>
              {this.props.route.params.brand == "" ? (
                <FloatingTextBox
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  label="Company"
                  placeholderTextColor="gray"
                  test={this.state.company}
                  onChangeText={(val) => {
                    this.setState({
                      company: val.toLowerCase().trim(),
                    });
                  }}
                ></FloatingTextBox>
              ) : (
                <FloatingTextBox
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  test={this.state.company}
                  label="Company"
                  value={this.state.company}
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      company: val.toLowerCase().trim(),
                    });
                  }}
                  defaultValue={this.props.route.params.brand}
                ></FloatingTextBox>
              )}
            </View>
            <View style={{ marginVertical: "5%" }}>
              <FloatingTextBox
                style={{
                  height: 40,
                  borderColor: "white",
                  borderWidth: 2,
                  borderRadius: 5,
                  marginRight: 100,
                  marginVertical: "2%",
                  padding: "2%",
                  backgroundColor: "#272727",
                  color: "white",
                }}
                label="Genre"
                test={this.state.genre}
                placeholderTextColor="gray"
                onChangeText={(val) => {
                  this.setState({
                    genre: val.toLowerCase().trim(),
                  });
                }}
              ></FloatingTextBox>
            </View>
            <View style={{ marginVertical: "5%" }}>
              <FloatingTextInput
                style={{
                  height: 40,
                  borderColor: "white",
                  borderWidth: 2,
                  borderRadius: 5,
                  marginRight: 100,
                  marginVertical: "2%",
                  padding: "2%",
                  backgroundColor: "#272727",
                  color: "white",
                }}
                label="Year"
                value={this.state.year}
                placeholderTextColor="gray"
                maxLength={4}
                keyboardType="number-pad"
                onChangeText={(val) => {
                  this.setState({
                    year: val,
                  });
                }}
              ></FloatingTextInput>
            </View>
            <View style={{ marginVertical: "5%" }}>
              {this.props.route.params.platform == "" ? (
                <FloatingTextBox
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  label="Platform"
                  test={this.state.platform}
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      platform: val.toLowerCase().trim(),
                    });
                  }}
                ></FloatingTextBox>
              ) : (
                <FloatingTextBox
                  style={{
                    height: 40,
                    borderColor: "white",
                    borderWidth: 2,
                    borderRadius: 5,
                    marginRight: 100,
                    marginVertical: "2%",
                    padding: "2%",
                    backgroundColor: "#272727",
                    color: "white",
                  }}
                  label="Platform"
                  placeholderTextColor="gray"
                  onChangeText={(val) => {
                    this.setState({
                      platform: val.toLowerCase().trim(),
                    });
                  }}
                  test={this.state.platform}
                  defaultValue={this.props.route.params.platform}
                ></FloatingTextBox>
              )}
            </View>
            <View style={{ marginVertical: "5%" }}>
              <FloatingTextBox
                style={{
                  height: 150,
                  borderColor: "white",
                  borderWidth: 2,
                  borderRadius: 5,
                  marginRight: 100,
                  marginVertical: "2%",
                  padding: "2%",
                  backgroundColor: "#272727",
                  color: "white",
                }}
                label="Description"
                test={this.state.description}
                placeholderTextColor="gray"
                multiline={true}
                onChangeText={(val) => {
                  this.setState({
                    description: val.trim(),
                  });
                }}
              ></FloatingTextBox>
              <View
                style={{
                  justifyContent: "flex-start",
                  alignItems: "center",
                  flexDirection: "row",
                  marginVertical: "5%",
                }}
              >
                <Rating
                  type="custom"
                  ratingBackgroundColor="gray"
                  ratingCount={5}
                  fractions={1}
                  minValue={1}
                  startingValue={0}
                  imageSize={30}
                  onFinishRating={(rating) => {
                    this.setState({
                      rating: rating,
                    });
                  }}
                  showRating={true}
                  style={{ backgroundColor: "black" }}
                />
                <Text style={{ color: "red", marginRight: "5%" }}>*</Text>
              </View>

              <View
                style={{
                  marginTop: "5%",
                  flexDirection: "row",
                  justifyContent: "flex-start",
                }}
              >
                <Text
                  style={{ fontSize: 15, fontWeight: "bold", color: "white" }}
                >
                  Make Item Public
                </Text>
                <Switch
                  style={{ marginLeft: "10%" }}
                  trackColor={{ false: "#767577", true: "#81b0ff" }}
                  thumbColor={this.state.public ? "#f5dd4b" : "#f4f3f4"}
                  onValueChange={() => {
                    this.setState({
                      public: !this.state.public,
                    });
                  }}
                  value={this.state.public}
                />
              </View>
            </View>
            <View>
              <TouchableOpacity
                style={{
                  backgroundColor: "#BB86FC",
                  borderRadius: 5,
                  height: "20%",
                  width: "80%",
                  justifyContent: "center",
                  alignItems: "center",
                  marginVertical: "5%",
                }}
                onPress={this.handleAdd}
              >
                <Text style={{ fontSize: 20, fontWeight: "bold" }}>ADD</Text>
              </TouchableOpacity>
              <View style={{ marginVertical: "5%" }}></View>
            </View>
          </View>
        </ScrollView>
      </KeyboardAvoidingView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "black",
  },
});
